    // const width = event.currentTarget.width;
    // const id = event.currentTarget.id;
    // 이거를 이렇게 바꿔 쓰실수 있습니다.
    // const {
    // currentTarget: {value, tagName, width, id}
    // } = event;
  };
  const onSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    console.log("hello", value);
  };
  return <div></div>;
}

export default App;
