  0% {
    transform:rotate(0deg);
    border-radius: 0px;

  }
  50%{

    border-radius:100px;

  }
  100%{
    transform:rotate(360deg);
    border-radius:0px;
  }

`;
const Emoji = styled.span`
  font-size: 50px;
