import { useState } from "react";

function App() {
  const [value, setValue] = useState("");
  const onChange = (event: React.FormEvent<HTMLInputElement>) => {
    const {
      currentTarget: { value },
    } = event;
  };
  return (
    <div>
      <form>
        <input value={value} onChange={onChange} type="text"></input>
        <button>Log in</button>
      </form>
    </div>
  );
}

export default App;
